#' bw_opt <- gcv_locPoly1d(regBwCand, regularExData$t, regularExData$y, w, "gauss", 0, 1)
#' @export
gcv_locPoly1d <- function(bwCand, x, y, w, kernel, drv, degree) {
    .Call('rfda_gcv_locPoly1d', PACKAGE = 'rfda', bwCand, x, y, w, kernel, drv, degree)
}

